import React from "react";
import { connect } from "react-redux";
import AdditionalInfo from "../JobDetailed/AdditionalInfo/AdditionalInfo";
import {
  JobType,
  selectCurrnetTags,
  selectJobsList,
  setCurrentJob,
  setTag,
} from "../redux/jobSlice";
import { AppDispatch, RootState } from "../redux/store";
import Job from "./Job";

type JobContainerProps = {
  list: JobType[];
  tags: Array<string>;
  onSetCurrentJob: (data: JobType) => void;
  onSetTag: (tag: string) => void;
};

const intersection = (first: Array<any>, second: Array<any>): Array<any> => {
  return first.filter((x) => second.indexOf(x) !== -1);
};

const sortJobs = (
  first: Array<string>,
  second: Array<any>,
  third: Array<any>
) => {};

const JobContainer: React.FC<JobContainerProps> = (
  props: JobContainerProps
) => {
  const { list, tags, onSetCurrentJob, onSetTag } = props;

  return (
    <div>
      {!tags.length ? (
        <></>
      ) : (
        <AdditionalInfo
          {...{
            title: "Tags",
            data: [{ heading: "Selected tags", elements: tags }],
            onSetTag,
          }}
        />
      )}
      {list.map((item: JobType, idx) => {
        const { benefits, employment_type } = item;
        const common = intersection(tags, benefits.concat(employment_type));
        if (common.length && tags.length) {
          return (
            <Job data={item} onSetCurrentJob={onSetCurrentJob} key={idx} />
          );
        } else {
          return (
            <Job data={item} onSetCurrentJob={onSetCurrentJob} key={idx} />
          );
        }
      })}
    </div>
  );
};

const mapStatetoProps = (state: RootState) => ({
  list: selectJobsList(state),
  tags: selectCurrnetTags(state),
});

const mapDispatchToProps = (dispatch: AppDispatch) => ({
  onSetCurrentJob: (data: JobType) => {
    dispatch(setCurrentJob(data));
  },
  onSetTag: (tag: string) => {
    dispatch(setTag(tag));
  },
});

export default connect(mapStatetoProps, mapDispatchToProps)(JobContainer);
