import React, { useEffect, useRef } from "react";
import { connect } from "react-redux";
import { BrowserRouter } from "react-router-dom";
import "./App.css";
import Job from "./Job/Job";
import {
  selectInitializedSelector,
  thunkSetInitialized,
} from "./redux/initSlice";
import { AppDispatch, RootState } from "./redux/store";

function App(props: { initialized: boolean; onSetInitialized: any }) {
  const { initialized, onSetInitialized } = props;
  const refContainer = useRef(initialized);

  useEffect(() => {
    onSetInitialized(refContainer.current);
  }, [onSetInitialized]);

  return (
    <BrowserRouter>
      <Routes>
        <Route path="/profile" element={<ProfileContainer />}>
          <Route path=":userId" element={<ProfileContainer />} />
        </Route>
        <Route path="/dialogs" element={<DialogsContainer />} />
        <Route path="/users" element={<UsersContainer />} />
        <Route path="/login" element={<LoginContainer />} />
        <Route path="/register" element={<RegisterContainer />} />
      </Routes>
    </BrowserRouter>
  );
}
const mapStatetoProps = (state: RootState) => ({
  initialized: selectInitializedSelector(state),
});

const mapDispatchToProps = (dispatch: AppDispatch) => ({
  onSetInitialized: (flag: boolean) => {
    dispatch(thunkSetInitialized(flag));
  },
});

export default connect(mapStatetoProps, mapDispatchToProps)(App);
