import { createSlice, PayloadAction } from "@reduxjs/toolkit";
import { AppThunk, RootState } from "./store";
import getJobsRequest from "../requestAPI/requestAPI";

export type Location = {
  lat: number;
  long: number;
};

export type JobType = {
  id: string;
  name: string;
  email: string;
  phone: string;
  title: string;
  salary: string;
  address: string;
  benefits: Array<string>;
  location: JobType;
  pictures: Array<string>;
  createdAt: string;
  updatedAt: string;
  description: string;
  employment_type: Array<string>;
};

export type JobsState = {
  list: Array<JobType>;
  currentJob: JobType | null;
  tags: Array<string>;
};

const initialState: JobsState = {
  list: [],
  currentJob: null,
  tags: [],
};

export const jobsSlice = createSlice({
  name: "jobs",
  initialState,
  reducers: {
    setJobsList: (state, action: PayloadAction<Array<JobType>>) => {
      state.list = action.payload;
    },
    setCurrentJob: (state, action: PayloadAction<JobType>) => {
      state.currentJob = action.payload;
    },
    setNewTags: (state, action: PayloadAction<string>) => {
      state.tags.push(action.payload);
    },
    deleteCurrentTags: (state, action: PayloadAction<string>) => {
      state.tags.filter((element) => element !== action.payload);
    },
  },
});

export const { setJobsList, setCurrentJob, deleteCurrentTags } =
  jobsSlice.actions;

export const selectJobsList = (state: RootState) => state.jobs.list;
export const selectCurrentJob = (state: RootState) => state.jobs.currentJob;
export const selectCurrnetTags = (state: RootState) => state.jobs.tags;

export const thunkSetJobs = (): AppThunk => async (dispatch) => {
  const asyncResp = await getJobsRequest();
  dispatch(setJobsList(asyncResp));
};

export default jobsSlice.reducer;
