import { createSlice, PayloadAction } from "@reduxjs/toolkit";
import { AppThunk, RootState } from "./store";
import getJobsRequest from "../requestAPI/requestAPI";
import { useJsApiLoader } from "@react-google-maps/api";
import { resolve } from "path";
import { getGeocode } from "use-places-autocomplete";
import { locations } from "../data/locations";

export type LocationType = {
  lat: number;
  lng: number;
};

export type JobType = {
  id: string;
  name: string;
  email: string;
  phone: string;
  title: string;
  salary: string;
  address: string;
  benefits: Array<string>;
  location: LocationType;
  pictures: Array<string>;
  createdAt: string;
  updatedAt: string;
  description: string;
  employment_type: Array<string>;
};

export type JobsState = {
  list: Array<JobType>;
  currentJob: JobType | null;
  tags: Array<string>;
};

const initialState: JobsState = {
  list: [],
  currentJob: null,
  tags: [],
};

export const jobsSlice = createSlice({
  name: "jobs",
  initialState,
  reducers: {
    setJobsList: (state, action: PayloadAction<Array<JobType>>) => {
      state.list = action.payload;
    },
    setCurrentJob: (state, action: PayloadAction<JobType>) => {
      state.currentJob = action.payload;
    },
    setTag: (state, action: PayloadAction<string>) => {
      const tag = action.payload;
      if (state.tags.includes(tag)) {
        state.tags = state.tags.filter((item) => item !== tag);
      } else {
        state.tags.push(tag);
      }
    },
  },
});
let flag = false;
const formAddress = async (location: LocationType): Promise<any> => {
  try {
    console.log("flag", flag);
    if (!flag) {
      console.log("try");
      const address = await getGeocode({
        location,
      });
      flag = true;
      return address;
    }
    return "";
  } catch (error) {
    setTimeout(() => {
      formAddress(location);
    }, 1000);
  }
};

export const { setJobsList, setCurrentJob, setTag } = jobsSlice.actions;

export const selectJobsList = (state: RootState) => state.jobs.list;
export const selectCurrentJob = (state: RootState) => state.jobs.currentJob;
export const selectCurrnetTags = (state: RootState) => state.jobs.tags;

export const thunkSetJobs = (): AppThunk => async (dispatch) => {
  let list = await getJobsRequest();

  list.map((item, idx) => {
    item.location = locations[idx];
    return item;
  });

  for (let item of list) {
    await formAddress(item.location).then((data) => console.log(data[0]));
  }

  dispatch(setJobsList(list));
};

export default jobsSlice.reducer;
