import React from "react";
import Header from "../Header/Header";
import styles from "./AdditionalInfo.module.scss";

type Tag = {
  heading: string;
  elements: Array<string>;
};

type AdditionalInfoProps = {
  title: string;
  data: Array<Tag> | Array<string>;
  onSetTag: (tag: string) => void;
};

const AdditionalInfo: React.FC<AdditionalInfoProps> = (
  props: AdditionalInfoProps
) => {
  const { title, data, onSetTag } = props;

  return (
    <div>
      <Header
        {...{
          title,
          subTitles: null,
        }}
      />
      <div className={styles.blockOfInfo}>
        {data.map((item) => {
          if (typeof item !== "string") {
            const { heading, elements } = item;
            return (
              <div>
                <p>{heading}</p>
                <div className={styles.blockOfData}>
                  <div>
                    {elements.map((text) => (
                      <button
                        className={styles.tag}
                        onClick={() => onSetTag(text)}
                      >
                        {text}
                      </button>
                    ))}
                  </div>
                </div>
              </div>
            );
          }
          return <></>;
        })}
        <div className={styles.blockOfData}>
          {data.map((source) => {
            if (typeof source === "string") {
              return (
                <img src={source} alt="There must be something interesting!" />
              );
            }
            return <></>;
          })}
        </div>
      </div>
    </div>
  );
};

export default AdditionalInfo;
