import React from "react";
import { countDate } from "../../Job";
import Text from "../Text/Text";
import Title from "../Title/Titile";
import styles from "./Body.module.scss";

export type BodyProps = {
  title: string;
  salary: string;
  parsedDescription: Array<Array<String>>;
  createdAt: string;
  updatedAt: string;
};

const Body: React.FC<BodyProps> = (props: BodyProps) => {
  const { title, salary, parsedDescription, createdAt, updatedAt } = props;
  const date = countDate(createdAt, updatedAt);

  return (
    <div className={styles.body}>
      <button className={styles.apply_btn}>Apply now</button>
      <Title {...{ title, salary, date }} />
      <Text {...{ text: parsedDescription[0], list: false }} />
      <Text {...{ text: parsedDescription[1], list: false }} />
      <Text {...{ text: parsedDescription[2], list: true }} />
      <button className={styles.apply_btn}>Apply now</button>
    </div>
  );
};

export default Body;
