import React from "react";
import { connect } from "react-redux";
import { NavLink } from "react-router-dom";
import { JobType, selectCurrentJob } from "../../redux/jobSlice";
import { AppDispatch, RootState } from "../../redux/store";
import AdditionalInfo from "./AdditionalInfo/AdditionalInfo";
import Body from "./Body/Body";
import Header from "./Header/Header";
import styles from "./JobDetailed.module.scss";

type JobDetailedProps = {
  currentJob: JobType | null;
};

const JobDetailed: React.FC<JobDetailedProps> = (props: JobDetailedProps) => {
  const { currentJob } = props;

  const parseSalary = (data: string): string => {
    return data.replaceAll("k", " 000");
  };

  const parseDescription = (date: string): Array<Array<string>> => {
    const parsedData: Array<Array<string>> = [];
    const parsedText = date
      .split(/\r\n|\t|\r|\n/g)
      .map((item) => item.trimStart().trimEnd())
      .filter((item) => item !== "");
    for (let i = 0; i < parsedText.length; i++) {
      const current = parsedText[i];
      const next = parsedText[i + 1];
      if (current.includes(":") && !next.includes(":")) {
        parsedData.push([current, next]);
        i = parsedText.indexOf(next);
        continue;
      }
      parsedData.push([current]);
    }
    return parsedData;
  };

  if (!currentJob) return <div>pidor</div>;
  const {
    title,
    salary,
    description,
    createdAt,
    updatedAt,
    benefits,
    employment_type,
    pictures,
  } = currentJob;

  return (
    <div className={styles.container}>
      <div className={styles.information}>
        <Header
          {...{
            title: "Job Details",
            subTitles: [
              [
                "M17,18L12,15.82L7,18V5H17M17,3H7A2,2 0 0,0 5,5V21L12,18L19,21V5C19,3.89 18.1,3 17,3Z",
                "Save to my list",
              ],
              [
                "M17,18L12,15.82L7,18V5H17M17,3H7A2,2 0 0,0 5,5V21L12,18L19,21V5C19,3.89 18.1,3 17,3Z",
                "Share",
              ],
            ],
          }}
        />
        <Body
          {...{
            title,
            salary: parseSalary(salary),
            createdAt,
            updatedAt,
            parsedDescription: parseDescription(description),
          }}
        />
        <AdditionalInfo
          {...{
            title: "Additional info",
            data: [
              { heading: "Employment type", elements: employment_type },
              { heading: "Benefits", elements: benefits },
            ],
          }}
        />
        <AdditionalInfo {...{ title: "Attached images", data: pictures }} />
        <NavLink to={".../jobs"}>
          <div className={styles.return_btn}>
            <p> RETURN TO JOB BOADR</p>
          </div>
        </NavLink>
      </div>
    </div>
  );
};

const mapStatetoProps = (state: RootState) => ({
  currentJob: selectCurrentJob(state),
});

export default connect(mapStatetoProps, {})(JobDetailed);
