import { SvgIcon } from "@mui/material";
import React from "react";
import { connect } from "react-redux";
import { NavLink } from "react-router-dom";
import { JobType, selectCurrentJob } from "../redux/jobSlice";
import { RootState } from "../redux/store";
import AdditionalInfo from "./AdditionalInfo/AdditionalInfo";
import Body from "./Body/Body";
import Header from "./Header/Header";
import styles from "./JobDetailed.module.scss";

type JobDetailedProps = {
  title: string;
  salary: string;
  description: Array<Array<string>>;
  pictures: Array<string>;
  date: string;
};

const JobDetailed: React.FC<JobDetailedProps> = (props: JobDetailedProps) => {
  const { title, salary, description, pictures, date } = props;

  return (
    <div className={styles.container}>
      <div className={styles.information}>
        <Header
          {...{
            title: "Job Details",
            subTitles: [
              [
                "M17,18L12,15.82L7,18V5H17M17,3H7A2,2 0 0,0 5,5V21L12,18L19,21V5C19,3.89 18.1,3 17,3Z",
                "Save to my list",
              ],
              [
                "M17,18L12,15.82L7,18V5H17M17,3H7A2,2 0 0,0 5,5V21L12,18L19,21V5C19,3.89 18.1,3 17,3Z",
                "Share",
              ],
            ],
          }}
        />
        <Body
          {...{
            title,
            salary,
            date,
            description,
          }}
        />
        <AdditionalInfo
          {...{
            title: "Additional info",
            data: [
              { heading: "Employment type", elements: employment_type },
              { heading: "Benefits", elements: benefits },
            ],
          }}
        />
        <AdditionalInfo {...{ title: "Attached images", data: pictures }} />
        <NavLink to={"/jobs"}>
          <div className={styles.return_btn}>
            <SvgIcon>
              <path
                d={
                  "M15.41,16.58L10.83,12L15.41,7.41L14,6L8,12L14,18L15.41,16.58Z"
                }
              />
            </SvgIcon>
            <p> RETURN TO JOB BOADR</p>
          </div>
        </NavLink>
      </div>
    </div>
  );
};

export default JobDetailed;
