const memoizeAsync = (fn: any) => {
  const cache: any = {};
  return (...args: any) => {
    console.dir({ call: fnName, args, cache });
    const cb = args.pop();
    const key = args[0];
    const record = cache[key];
    console.log("key:", key);
    console.log("cached:", record);
    if (record) {
      console.log("from cache");
      cb(record.err, record.data);
      return;
    }
    fn(...args, (err: any, data: any) => {
      console.log("from file");
      console.log("Save key:", key);
      cache[key] = { err, data };
      console.dir({ cache });
      cb(err, data);
    });
  };
};

