import React from "react";
import { connect } from "react-redux";
import { JobType, selectCurrentJob, setTag } from "../redux/jobSlice";
import { AppDispatch, RootState } from "../redux/store";
import { countDate } from "../Job/Job";
import JobDetailedPage from "./JobDetailedPage";
import { Utils } from "../utils/utils";

type JobDetailedContainerProps = {
  currentJob: JobType | null;
  onSetTag: (tag: string) => void;
};

const JobDetailedContainer: React.FC<JobDetailedContainerProps> = (
  props: JobDetailedContainerProps
) => {
  const { currentJob, onSetTag } = props;

  // const parseSalary = (data: string): string => data.replaceAll("k", " 000");

  // const parseDescription = (date: string): Array<Array<string>> => {
  //   const parsedData: Array<Array<string>> = [];
  //   const parsedText = date
  //     .split(/\r\n|\t|\r|\n/g)
  //     .map((item) => item.trimStart().trimEnd())
  //     .filter((item) => item !== "");
  //   for (let i = 0; i < parsedText.length; i++) {
  //     const current = parsedText[i];
  //     const next = parsedText[i + 1];
  //     if (current.includes(":") && !next.includes(":")) {
  //       parsedData.push([current, next]);
  //       i = parsedText.indexOf(next);
  //       continue;
  //     }
  //     parsedData.push([current]);
  //   }
  //   return parsedData;
  // };

  if (!currentJob) return <div>pidor</div>;

  const {
    title,
    salary,
    description,
    createdAt,
    updatedAt,
    benefits,
    employment_type,
    pictures,
    location,
  } = currentJob;

  const date = countDate(createdAt, updatedAt);
  const parsedSalary = Utils.parseSalary(salary);
  const parsedDescription = Utils.parseDescription(description);

  return (
    <JobDetailedPage
      {...{
        title,
        salary: parsedSalary,
        date,
        description: parsedDescription,
        pictures,
        benefits,
        employment_type,
        onSetTag,
        location,
      }}
    />
  );
};

const mapStatetoProps = (state: RootState) => ({
  currentJob: selectCurrentJob(state),
});

const mapDispatchToProps = (dispatch: AppDispatch) => ({
  onSetTag: (tag: string) => {
    dispatch(setTag(tag));
  },
});

export default connect(
  mapStatetoProps,
  mapDispatchToProps
)(JobDetailedContainer);
