import { Icon, Rating, SvgIcon } from "@mui/material";
import React from "react";
import { JobType } from "../redux/jobSlice";
import styles from "./Jobs.module.scss";
import DeleteIcon from "@mui/icons-material/Delete";

const Job: React.FC<JobType> = (props: JobType) => {
  const createdData: Date = new Date(props.createdAt);
  const updateData: Date = new Date(props.updatedAt);

  const countDate = (updated: Date, created: Date): string => {
    const differInSecs: number = (updated.getTime() - created.getTime()) / 1000;
    const days: number = differInSecs / 86400;
    const end: string = days > 1 ? "days" : "day";
    return `${Math.floor(days)} ${end}`;
  };

  return (
    <div className={styles.container}>
      <img className={styles.image} src={props.pictures[0]}></img>
      <div className={styles.description}>
        <h1>{props.title}</h1>
        <div className={styles.departament}>
          <p>Departament name</p>
          <li>{props.name}</li>
        </div>
        <div className={styles.location}>
          <SvgIcon>
            <path d="M12,11.5A2.5,2.5 0 0,1 9.5,9A2.5,2.5 0 0,1 12,6.5A2.5,2.5 0 0,1 14.5,9A2.5,2.5 0 0,1 12,11.5M12,2A7,7 0 0,0 5,9C5,14.25 12,22 12,22C12,22 19,14.25 19,9A7,7 0 0,0 12,2Z" />
          </SvgIcon>
          <p>Location</p>
        </div>
      </div>
      <Rating
        className={styles.rating}
        name="simple-controlled"
        value={0}
        onChange={(event, newValue) => {}}
      />
      <div className={styles.data}>
        <SvgIcon className={styles.save}>
          <path d="M17,18L12,15.82L7,18V5H17M17,3H7A2,2 0 0,0 5,5V21L12,18L19,21V5C19,3.89 18.1,3 17,3Z" />
        </SvgIcon>
        <p>{`Posted ${countDate(updateData, createdData)} ago`}</p>
      </div>
    </div>
  );
};

export default Job;
