import { SvgIcon } from "@mui/material";
import React from "react";
import { connect } from "react-redux";
import { JobType, selectCurrentJob } from "../../redux/jobSlice";
import { AppDispatch, RootState } from "../../redux/store";
import styles from "./JobDetailed.module.scss";

type JobDetailedProps = {
  currentJob: JobType | null;
};

const JobDetailed: React.FC<JobDetailedProps> = (props: JobDetailedProps) => {
  const { currentJob } = props;

  const salary = (data: string): string => {
    return data.replaceAll("k", " 000");
  };

  const parseDescription = (date: string): Array<String> => {
    return date
      .split(/\r\n|\t|\r|\n/g)
      .map((item) => item.trimStart().trimEnd())
      .filter((item) => item !== "" && !item.includes(":"));
  };

  if (!currentJob) return <div>pidor</div>;
  const parsedDescription = parseDescription(currentJob.description);

  return (
    <div className={styles.container}>
      <div className={styles.information}>
        <div className={styles.header}>
          <h1>Job Details</h1>
          <div className={styles.header_data}>
            <span>
              <SvgIcon className={styles.save}>
                <path d="M17,18L12,15.82L7,18V5H17M17,3H7A2,2 0 0,0 5,5V21L12,18L19,21V5C19,3.89 18.1,3 17,3Z" />
              </SvgIcon>
              <p>Save to my list</p>
            </span>
            <span>
              <SvgIcon className={styles.share}>
                <path d="M17,18L12,15.82L7,18V5H17M17,3H7A2,2 0 0,0 5,5V21L12,18L19,21V5C19,3.89 18.1,3 17,3Z" />
              </SvgIcon>
              <p>Share</p>
            </span>
          </div>
        </div>
        <div className={styles.body}>
          <button className={styles.apply_btn}>Apply now</button>
          <div className={styles.data}>
            <div className={styles.main_data}>
              <h1>{currentJob.title}</h1>
              <p>Posted ago</p>
            </div>
            <div className={styles.salary}>
              <h1>
                <SvgIcon>
                  <path d="M15 18.5C12.5 18.5 10.32 17.08 9.24 15H15L16 13H8.58C8.53 12.67 8.5 12.34 8.5 12S8.53 11.33 8.58 11H15L16 9H9.24C10.32 6.92 12.5 5.5 15 5.5C16.61 5.5 18.09 6.09 19.23 7.07L21 5.3C19.41 3.87 17.3 3 15 3C11.08 3 7.76 5.5 6.5 9H3L2 11H6.06C6 11.33 6 11.66 6 12S6 12.67 6.06 13H3L2 15H6.5C7.76 18.5 11.08 21 15 21C17.31 21 19.41 20.13 21 18.7L19.22 16.93C18.09 17.91 16.62 18.5 15 18.5Z" />
                </SvgIcon>
                {salary(currentJob.salary)}
              </h1>
              <p>Brutto, per year</p>
            </div>
          </div>
          <p className={styles.text}>{parsedDescription[0]}</p>
          <div className={styles.text}>
            <h1>Responsibilities:</h1>
            <p>{parsedDescription[1]}</p>
          </div>
          <div className={styles.text}>
            <h1>Compensation & Benefits:</h1>
            <p>{parsedDescription[2]}</p>
          </div>
          <div className={styles.additional_info}></div>
          <button className={styles.apply_btn}>Apply now</button>
        </div>
      </div>
    </div>
  );
};

const mapStatetoProps = (state: RootState) => ({
  currentJob: selectCurrentJob(state),
});

const mapDispatchToProps = (dispatch: AppDispatch) => ({});

export default connect(mapStatetoProps, mapDispatchToProps)(JobDetailed);
