import React from "react";
import { JobType } from "../redux/jobSlice";

const Job: React.FC<JobType> = (props: JobType) => {
  const createdData: Date = new Date(props.createdAt);
  const updateData: Date = new Date();

  const countDate = (updated: Date, created: Date) => {
    const differInSecs: any = Math.floor(
      (updated.getTime() - created.getTime()) / 1000
    );
    const days: any = differInSecs / 86400;
    return days;
  };

  return (
    <div className="container">
      <img className="image"></img>
      <div className="description-container">
        <h1>{props.title}</h1>
        <div className="details-container">
          <p>Departament name:</p>
          <p>{props.address}</p>
          <div className="data">{countDate(updateData, createdData)}</div>
        </div>
      </div>
    </div>
  );
};

export default Job;
