import { createSlice, PayloadAction } from "@reduxjs/toolkit";
import { AppThunk, RootState } from "./store";

export type appState = {
  initialized: boolean;
};

const initialState: appState = {
  initialized: false,
};
export const appSlice = createSlice({
  name: "app",
  initialState,
  reducers: {
    setInitialized: (state, action: PayloadAction<appState>) => {
      state.initialized = action.payload.initialized;
    },
  },
});

export const { setInitialized } = appSlice.actions;

export const selectInitializedSelector = (state: RootState) => state.app;

export const thunkSetInitialized = (): AppThunk => async (dispatch) => {
  const asyncResp = await getJobsRequest();
  dispatch(setJobsList(asyncResp));
};

export default appSlice.reducer;
