import { SvgIcon } from "@mui/material";
import React from "react";
import { NavLink } from "react-router-dom";
import iconPath from "../sources/svgIcons";
import AdditionalInfo from "./AdditionalInfo/AdditionalInfo";
import Body from "./Body/Body";
import Header from "./Header/Header";
import styles from "./JobDetailed.module.scss";

type JobDetailedProps = {
  title: string;
  salary: string;
  date: string;
  description: Array<Array<string>>;
  pictures: Array<string>;
  benefits: Array<string>;
  employment_type: Array<string>;
  onSetTag: (tag: string) => void;
};

const JobDetailedPage: React.FC<JobDetailedProps> = (
  props: JobDetailedProps
) => {
  const {
    title,
    salary,
    description,
    pictures,
    date,
    employment_type,
    benefits,
    onSetTag,
  } = props;

  return (
    <div className={styles.container}>
      <div className={styles.information}>
        <Header
          {...{
            title: "Job Details",
            subTitles: [
              [iconPath.save, "Save to my list"],
              [iconPath.share, "Share"],
            ],
          }}
        />
        <Body
          {...{
            title,
            salary,
            date,
            description,
          }}
        />
        <AdditionalInfo
          {...{
            title: "Additional info",
            data: [
              {
                heading: "Employment type",
                elements: employment_type,
                style:
                  "flex flex-grow text-center py-3 w-1/5 border border-blue-500 rounded-xl text-blue-500 bg-blue-100",
              },
              {
                heading: "Benefits",
                elements: benefits,
                style:
                  "flex flex-grow text-center py-3 w-1/5 border border-yellow-500 rounded-xl text-yellow-500 bg-yellow-100",
              },
            ],
            onSetTag,
          }}
        />
        <AdditionalInfo
          {...{ title: "Attached images", data: pictures, onSetTag }}
        />
        <NavLink to="/jobs">
          <div className={styles.return_btn}>
            <SvgIcon>
              <path d={iconPath.leftArrow} />
            </SvgIcon>
            <p>RETURN TO JOB BOARD</p>
          </div>
        </NavLink>
      </div>
    </div>
  );
};

export default JobDetailedPage;
