import { SvgIcon } from "@mui/material";
import React from "react";
import { connect } from "react-redux";
import { JobType, selectCurrentJob } from "../../redux/jobSlice";
import { AppDispatch, RootState } from "../../redux/store";
import styles from "./JobDetailed.module.scss";

type JobDetailedProps = {
  currentJob: JobType | null;
};

const JobDetailed: React.FC<JobDetailedProps> = (props: JobDetailedProps) => {
  const { currentJob } = props;

  const salary = (data: string): string => {
    return data.replaceAll("k", " 000");
  };

  const parseDescription = (date: string): Array<String> => {
    return date
      .split(/\r\n|\t|\r|\n/g)
      .map((item) => item.trimStart().trimEnd())
      .filter((item) => item !== "" && !item.includes(":"));
  };

  if (!currentJob) return <div>pidor</div>;
  const parsedDescription = parseDescription(currentJob.description);

  return (
    <div className={styles.container}>
      <div className={styles.information}>
        <div className={styles.header}>
          <h1>Job Details</h1>
          <div className={styles.header_data}>
            <span>
              <SvgIcon className={styles.save}>
                <path d="M17,18L12,15.82L7,18V5H17M17,3H7A2,2 0 0,0 5,5V21L12,18L19,21V5C19,3.89 18.1,3 17,3Z" />
              </SvgIcon>
              <p>Save to my list</p>
            </span>
            <span>
              <SvgIcon className={styles.share}>
                <path d="M17,18L12,15.82L7,18V5H17M17,3H7A2,2 0 0,0 5,5V21L12,18L19,21V5C19,3.89 18.1,3 17,3Z" />
              </SvgIcon>
              <p>Share</p>
            </span>
          </div>
        </div>
      </div>
    </div>
  );
};

const mapStatetoProps = (state: RootState) => ({
  currentJob: selectCurrentJob(state),
});

const mapDispatchToProps = (dispatch: AppDispatch) => ({});

export default connect(mapStatetoProps, mapDispatchToProps)(JobDetailed);
