import React, { useEffect, useState } from "react";
import { connect } from "react-redux";
import { getGeocode } from "use-places-autocomplete";
import AdditionalInfo from "../JobDetailed/AdditionalInfo/AdditionalInfo";
import {
  JobType,
  LocationType,
  selectCurrnetTags,
  selectJobsList,
  setCurrentJob,
  setTag,
} from "../redux/jobSlice";
import { AppDispatch, RootState } from "../redux/store";
import Job from "./Job";

type JobContainerProps = {
  list: JobType[];
  tags: Array<string>;
  onSetCurrentJob: (data: JobType) => void;
  onSetTag: (tag: string) => void;
};

const compareTags = (first: Array<string>, second: Array<string>): boolean => {
  for (const element of first) {
    if (!second.includes(element)) return false;
  }
  return true;
};

const sortJobs = (
  list: Array<JobType>,
  selectedTags: Array<string>
): Array<JobType> => {
  const res: Array<JobType> = [];
  for (const job of list) {
    const { benefits, employment_type } = job;
    const jobTags = benefits.concat(employment_type);
    if (compareTags(selectedTags, jobTags)) {
      res.push(job);
    }
  }
  return res;
};

const JobContainer: React.FC<JobContainerProps> = (
  props: JobContainerProps
) => {
  const { list, tags, onSetCurrentJob, onSetTag } = props;
  const sortedList = sortJobs(list, tags);
  const getFormattedAddress = async (location: LocationType) => {
    let address = "";
    await getGeocode({
      location,
    })
      .then((data) => {
        return data[0].formatted_address;
      })
      .then((formattedAddress) => {
        address = formattedAddress;
      });
    return address;
  };

  return (
    <div>
      {!tags.length ? (
        <></>
      ) : (
        <AdditionalInfo
          {...{
            title: "Tags",
            data: [
              {
                heading: "Selected tags",
                elements: tags,
                style:
                  "max-w-1/3 min-w-fit text-center p-3 w-1/5 border border-blue-500 rounded-xl text-blue-500 bg-blue-100  hover:bg-blue-300 active:ring-4 focus:ring-blue-300",
              },
            ],
            onSetTag,
          }}
        />
      )}
      {sortedList.length
        ? sortedList.map((item: JobType, idx) => (
            <Job
              data={{ ...item }}
              country={getFormattedAddress(item.location)}
              onSetCurrentJob={onSetCurrentJob}
              key={idx}
            />
          ))
        : list.map((item: JobType, idx) => (
            <Job
              data={{ ...item }}
              country={getFormattedAddress(item.location)}
              onSetCurrentJob={onSetCurrentJob}
              key={idx}
            />
          ))}
    </div>
  );
};

const mapStatetoProps = (state: RootState) => ({
  list: selectJobsList(state),
  tags: selectCurrnetTags(state),
});

const mapDispatchToProps = (dispatch: AppDispatch) => ({
  onSetCurrentJob: (data: JobType) => {
    dispatch(setCurrentJob(data));
  },
  onSetTag: (tag: string) => {
    dispatch(setTag(tag));
  },
});

export default connect(mapStatetoProps, mapDispatchToProps)(JobContainer);
