import { createSlice, PayloadAction } from "@reduxjs/toolkit";
import { AppThunk, RootState } from "./store";

export type initState = {
  initialized: boolean;
};

const initialState: initState = {
  initialized: false,
};
export const initSlice = createSlice({
  name: "app",
  initialState,
  reducers: {
    setInitialized: (state, action: PayloadAction<initState>) => {
      state.initialized = action.payload.initialized;
    },
  },
});

export const { setInitialized } = initSlice.actions;

export const selectInitialized = (state: RootState) => state.initialized;

export const thunkSetJobs = (): AppThunk => async (dispatch) => {
  const asyncResp = await getJobsRequest();
  dispatch(setJobsList(asyncResp));
};

export default jobsSlice.reducer;
