import React from "react";
import Header from "../Header/Header";
import styles from "./AdditionalInfo.module.scss";

type Tag = {
  heading: string;
  data: Array<string>;
};

type AdditionalInfoProps = {
  title: string;
  data: Array<Tag> | Array<string>;
};

const AdditionalInfo: React.FC<AdditionalInfoProps> = (
  props: AdditionalInfoProps
) => {
  const { title, data } = props;
  return (
    <div>
      <Header
        {...{
          title,
          subTitles: null,
        }}
      />
      <div className={styles.blockOfInfo}>
        {typeof data !== "Array<string>"
          ? data.map((item) => {
              const { heading, data } = item;
              return (
                <div>
                  <p>{heading}</p>
                  <div className={styles.blockOfTags}>
                    {data.map((text) => {
                      return <div className={styles.tag}>{text}</div>;
                    })}
                  </div>
                </div>
              );
            })
          : data.map((source) => (
              <img src={source} alt="There must be photo!" />
            ))}
        {data.map((item) => {
          if (typeof item !== "string") {
            const { heading, data } = item;
            return (
              <div>
                <p>{heading}</p>
                <div className={styles.blockOfTags}>
                  {data.map((text) => {
                    return <div className={styles.tag}>{text}</div>;
                  })}
                </div>
              </div>
            );
          }
          return;
        })}
        <div className={styles.tag}></div>
      </div>
    </div>
  );
};

export default AdditionalInfo;
