import { SvgIcon } from "@mui/material";
import React, { useEffect, useState } from "react";
import { connect } from "react-redux";
import iconPath from "../data/svgIcons";
import AdditionalInfo from "../JobDetailed/AdditionalInfo/AdditionalInfo";
import { usePagination } from "../redux/hooks";
import {
  JobType,
  selectCurrnetTags,
  selectJobsList,
  setCurrentJob,
  setTag,
} from "../redux/jobSlice";
import { AppDispatch, RootState } from "../redux/store";
import { Utils } from "../utils/utils";
import Job from "./Job";
import styles from "./Jobs.module.scss";

interface JobContainerProps {
  list: JobType[];
  tags: string[];
  onSetCurrentJob: (data: JobType) => void;
  onSetTag: (tag: string) => void;
}

const JobContainer: React.FC<JobContainerProps> = (
  props: JobContainerProps
) => {
  const [list, setList] = useState<JobType[]>([]);
  const [tags, setTags] = useState<string[]>([]);
  const sortedList = Utils.sortJobs(list, tags);
  const {
    firstContentIndex,
    lastContentIndex,
    nextPage,
    prevPage,
    page,
    setPage,
    totalPages,
  } = usePagination({
    contentPerPage: 4,
    count: tags ? sortedList.length : list.length,
  });

  useEffect(() => {
    setList(props.list);
    setTags(props.tags);
  }, [props.list, props.tags]);

  return (
    <div>
      {!tags.length ? (
        <></>
      ) : (
        <AdditionalInfo
          {...{
            title: "Tags",
            data: [
              {
                heading: "Selected tags",
                elements: tags,
                style:
                  "max-w-1/3 min-w-fit text-center p-3 w-1/5 border border-blue-500 rounded-xl text-blue-500 bg-blue-100  hover:bg-blue-300 active:ring-4 focus:ring-blue-300",
              },
            ],
            onSetTag: props.onSetTag,
          }}
        />
      )}
      {sortedList.length
        ? sortedList
            .slice(firstContentIndex, lastContentIndex)
            .map((item: JobType, idx) => (
              <Job
                data={{ ...item }}
                onSetCurrentJob={props.onSetCurrentJob}
                key={idx}
              />
            ))
        : list.map((item: JobType, idx) => (
            <Job
              data={{ ...item }}
              onSetCurrentJob={props.onSetCurrentJob}
              key={idx}
            />
          ))}
      <div className={styles.pagination}>
        <button onClick={prevPage} className={styles.arrow}>
          <SvgIcon>
            <path d={iconPath.leftArrow} />
          </SvgIcon>
        </button>
        {/* @ts-ignore */}
        {[...Array(totalPages).keys()].map((el) => (
          <button
            onClick={() => setPage(el + 1)}
            key={el}
            className={styles.page}
          >
            {el + 1}
          </button>
        ))}
        <button onClick={nextPage} className={styles.arrow}>
          <SvgIcon>
            <path d={iconPath.rightArrow} />
          </SvgIcon>
        </button>
      </div>
      <div className="flex items-center justify-between border-t border-gray-200 bg-white px-4 py-3 sm:px-6">
        <div className="flex flex-1 justify-between sm:hidden">
          <a
            href="#"
            className="relative inline-flex items-center rounded-md border border-gray-300 bg-white px-4 py-2 text-sm font-medium text-gray-700 hover:bg-gray-50"
          >
            Previous
          </a>
          <a
            href="#"
            className="relative ml-3 inline-flex items-center rounded-md border border-gray-300 bg-white px-4 py-2 text-sm font-medium text-gray-700 hover:bg-gray-50"
          >
            Next
          </a>
        </div>
        <div className="hidden sm:flex sm:flex-1 sm:items-center sm:justify-between">
          <div>
            <p className="text-sm text-gray-700">
              Showing <span className="font-medium">1</span> to{" "}
              <span className="font-medium">10</span> of{" "}
              <span className="font-medium">97</span> results
            </p>
          </div>
          <div>
            <nav
              className="isolate inline-flex -space-x-px rounded-md shadow-sm"
              aria-label="Pagination"
            >
              <a
                href="#"
                className="relative inline-flex items-center rounded-l-md border border-gray-300 bg-white px-2 py-2 text-sm font-medium text-gray-500 hover:bg-gray-50 focus:z-20"
              >
                <span className="sr-only">Previous</span>
                <ChevronLeftIcon className="h-5 w-5" aria-hidden="true" />
              </a>
              {/* Current: "z-10 bg-indigo-50 border-indigo-500 text-indigo-600", Default: "bg-white border-gray-300 text-gray-500 hover:bg-gray-50" */}
              <a
                href="#"
                aria-current="page"
                className="relative z-10 inline-flex items-center border border-indigo-500 bg-indigo-50 px-4 py-2 text-sm font-medium text-indigo-600 focus:z-20"
              >
                1
              </a>
              <a
                href="#"
                className="relative inline-flex items-center border border-gray-300 bg-white px-4 py-2 text-sm font-medium text-gray-500 hover:bg-gray-50 focus:z-20"
              >
                2
              </a>
              <a
                href="#"
                className="relative hidden items-center border border-gray-300 bg-white px-4 py-2 text-sm font-medium text-gray-500 hover:bg-gray-50 focus:z-20 md:inline-flex"
              >
                3
              </a>
              <span className="relative inline-flex items-center border border-gray-300 bg-white px-4 py-2 text-sm font-medium text-gray-700">
                ...
              </span>
              <a
                href="#"
                className="relative hidden items-center border border-gray-300 bg-white px-4 py-2 text-sm font-medium text-gray-500 hover:bg-gray-50 focus:z-20 md:inline-flex"
              >
                8
              </a>
              <a
                href="#"
                className="relative inline-flex items-center border border-gray-300 bg-white px-4 py-2 text-sm font-medium text-gray-500 hover:bg-gray-50 focus:z-20"
              >
                9
              </a>
              <a
                href="#"
                className="relative inline-flex items-center border border-gray-300 bg-white px-4 py-2 text-sm font-medium text-gray-500 hover:bg-gray-50 focus:z-20"
              >
                10
              </a>
              <a
                href="#"
                className="relative inline-flex items-center rounded-r-md border border-gray-300 bg-white px-2 py-2 text-sm font-medium text-gray-500 hover:bg-gray-50 focus:z-20"
              >
                <span className="sr-only">Next</span>
                <ChevronRightIcon className="h-5 w-5" aria-hidden="true" />
              </a>
            </nav>
          </div>
        </div>
      </div>
    </div>
  );
};

const mapStatetoProps = (state: RootState) => ({
  list: selectJobsList(state),
  tags: selectCurrnetTags(state),
});

const mapDispatchToProps = (dispatch: AppDispatch) => ({
  onSetCurrentJob: (data: JobType) => {
    dispatch(setCurrentJob(data));
  },
  onSetTag: (tag: string) => {
    dispatch(setTag(tag));
  },
});

export default connect(mapStatetoProps, mapDispatchToProps)(JobContainer);
