import { SvgIcon } from "@mui/material";
import React from "react";
import { NavLink } from "react-router-dom";
import AdditionalInfo from "./AdditionalInfo/AdditionalInfo";
import Body from "./Body/Body";
import Header from "./Header/Header";
import styles from "./JobDetailed.module.scss";

type JobDetailedProps = {
  title: string;
  salary: string;
  date: string;
  description: Array<Array<string>>;
  pictures: Array<string>;
  benefits: Array<string>;
  employment_type: Array<string>;
  onSetTag: (tag: string) => void;
};

const JobDetailedPage: React.FC<JobDetailedProps> = (
  props: JobDetailedProps
) => {
  const {
    title,
    salary,
    description,
    pictures,
    date,
    employment_type,
    benefits,
    onSetTag,
  } = props;

  return (
    <div className={styles.container}>
      <div className={styles.information}>
        <Header
          {...{
            title: "Job Details",
            subTitles: [
              [
                "M17,18L12,15.82L7,18V5H17M17,3H7A2,2 0 0,0 5,5V21L12,18L19,21V5C19,3.89 18.1,3 17,3Z",
                "Save to my list",
              ],
              [
                "M17,18L12,15.82L7,18V5H17M17,3H7A2,2 0 0,0 5,5V21L12,18L19,21V5C19,3.89 18.1,3 17,3Z",
                "Share",
              ],
            ],
          }}
        />
        <Body
          {...{
            title,
            salary,
            date,
            description,
          }}
        />
        <AdditionalInfo
          {...{
            title: "Additional info",
            data: [
              {
                heading: "Employment type",
                elements: employment_type,
                color: "blue",
              },
              {
                heading: "Benefits",
                elements: benefits,
                color: "slate",
              },
            ],
            onSetTag,
          }}
        />
        <AdditionalInfo
          {...{ title: "Attached images", data: pictures, onSetTag }}
        />
        <NavLink to="/jobs">
          <div className={styles.return_btn}>
            <SvgIcon>
              <path d="M15.41,16.58L10.83,12L15.41,7.41L14,6L8,12L14,18L15.41,16.58Z" />
            </SvgIcon>
            <p>RETURN TO JOB BOARD</p>
          </div>
        </NavLink>
      </div>
    </div>
  );
};

export default JobDetailedPage;
