import { StylisPlugin } from "@emotion/cache";
import { SvgIcon } from "@mui/material";
import React from "react";
import { connect } from "react-redux";
import { JobType } from "../../../../redux/jobSlice";
import styles from "./Body.module.scss";

type BodyProps = {
  title: string;
  salary: string;
  parsedDescription: Array<Array<String>>;
};

const Body: React.FC<BodyProps> = (props: BodyProps) => {
  const { title, salary, parsedDescription } = props;

  return (
    <div className={styles.body}>
      <button className={styles.apply_btn}>Apply now</button>
      <div className={styles.data}>
        <div className={styles.main_data}>
          <h1>{title}</h1>
          <p>Posted ago</p>
        </div>
        <div className={styles.salary}>
          <h1>
            <SvgIcon>
              <path d="M15 18.5C12.5 18.5 10.32 17.08 9.24 15H15L16 13H8.58C8.53 12.67 8.5 12.34 8.5 12S8.53 11.33 8.58 11H15L16 9H9.24C10.32 6.92 12.5 5.5 15 5.5C16.61 5.5 18.09 6.09 19.23 7.07L21 5.3C19.41 3.87 17.3 3 15 3C11.08 3 7.76 5.5 6.5 9H3L2 11H6.06C6 11.33 6 11.66 6 12S6 12.67 6.06 13H3L2 15H6.5C7.76 18.5 11.08 21 15 21C17.31 21 19.41 20.13 21 18.7L19.22 16.93C18.09 17.91 16.62 18.5 15 18.5Z" />
            </SvgIcon>
            {salary}
          </h1>
          <p>Brutto, per year</p>
        </div>
      </div>
      <div className={styles.text}>
        <p>{parsedDescription[0]}</p>
      </div>
      {parsedDescription.forEach((array: Array<String>) => {
        return (
          <div className={styles.text}>
            {array.length > 1 ? (
              <>
                <h1>{array[0]}</h1>
                <p>{array[1]}</p>
              </>
            ) : (
              <p>{array[0]}</p>
            )}
          </div>
        );
      })}
      <button className={styles.apply_btn}>Apply now</button>
    </div>
  );
};

export default Body;
