import { Rating, SvgIcon } from "@mui/material";
import { resolve } from "path";
import React, { useEffect, useState } from "react";
import { NavLink } from "react-router-dom";
import { getGeocode } from "use-places-autocomplete";
import { JobType, LocationType } from "../redux/jobSlice";
import iconPath from "../sources/svgIcons";
import styles from "./Jobs.module.scss";

type JobProps = {
  data: JobType;
  onSetCurrentJob: (data: JobType) => void;
};

export const countDate = (createdAt: string, updatedAt: string): string => {
  const createdData: Date = new Date(createdAt);
  const updatedData: Date = new Date(updatedAt);
  const differInSecs: number =
    (updatedData.getTime() - createdData.getTime()) / 1000;
  const days: number = differInSecs / 86400;
  const end: string = days > 1 ? "days" : "day";
  return `${Math.floor(days)} ${end}`;
};

const formAddress = async (location: LocationType): Promise<any> => {
  let flag = false;
  try {
    if (!flag) {
      const address = await getGeocode({
        location,
      });
      flag = true;
      return address;
    }
    return;
  } catch (error) {
    return new Promise(async (resolve) => {
      setTimeout(() => {
        resolve(formAddress(location));
      }, 10);
    });
  }
};

const Job: React.FC<JobProps> = (props: JobProps) => {
  const { data, onSetCurrentJob } = props;
  const { location } = data;
  const [address, setAddress] = useState("");

  useEffect(() => {
    console.log("render");
    formAddress(location).then((data) => {
      if (data) {
        const address = data[0].formatted_address;
        setAddress(address);
      }
    });
  }, [data]);

  return (
    <div className={styles.container}>
      <img className={styles.image} src={data.pictures[0]} />
      <div className={styles.main}>
        <div className={styles.description}>
          <NavLink
            end
            to={`./../jobs/${data.id}`}
            onClick={() => onSetCurrentJob({ ...data, location })}
          >
            <h1>{data.title}</h1>
          </NavLink>
          <div className={styles.departament}>
            <p>Departament name</p>
            <li>{data.name}</li>
          </div>
          <div className={styles.location}>
            <SvgIcon>
              <path d={iconPath.location} />
            </SvgIcon>
            <p>{address}</p>
          </div>
        </div>
        <div className={styles.data_column}>
          <div className={styles.rating}>
            <Rating
              size="small"
              name="simple-controlled"
              value={Math.ceil(Math.random() * 5)}
              onChange={(event, newValue) => {}}
            />
          </div>
          <div className={styles.data}>
            <SvgIcon className={styles.save}>
              <path d={iconPath.save} />
            </SvgIcon>
            <p>{`Posted ${countDate(data.createdAt, data.updatedAt)} ago`}</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Job;
