import { SvgIcon } from "@mui/material";
import React from "react";
import { connect } from "react-redux";
import { NavLink } from "react-router-dom";
import { JobType, selectCurrentJob } from "../redux/jobSlice";
import { RootState } from "../redux/store";
import { countDate } from "../Job/Job";
import AdditionalInfo from "./AdditionalInfo/AdditionalInfo";
import Body from "./Body/Body";
import Header from "./Header/Header";
import styles from "./JobDetailed.module.scss";
import JobDetailed from "./JobDetailed";

type JobDetailedContainerProps = {
  currentJob: JobType | null;
};

const JobDetailedContainer: React.FC<JobDetailedContainerProps> = (
  props: JobDetailedContainerProps
) => {
  const { currentJob } = props;

  const parseSalary = (data: string): string => {
    return data.replaceAll("k", " 000");
  };

  const parseDescription = (date: string): Array<Array<string>> => {
    const parsedData: Array<Array<string>> = [];
    const parsedText = date
      .split(/\r\n|\t|\r|\n/g)
      .map((item) => item.trimStart().trimEnd())
      .filter((item) => item !== "");
    for (let i = 0; i < parsedText.length; i++) {
      const current = parsedText[i];
      const next = parsedText[i + 1];
      if (current.includes(":") && !next.includes(":")) {
        parsedData.push([current, next]);
        i = parsedText.indexOf(next);
        continue;
      }
      parsedData.push([current]);
    }
    return parsedData;
  };

  if (!currentJob) return <div>pidor</div>;
  const {
    title,
    salary,
    description,
    createdAt,
    updatedAt,
    benefits,
    employment_type,
    pictures,
  } = currentJob;

  const date = countDate(createdAt, updatedAt);
  const parsedSalary = parseSalary(salary);
  const parsedDescription = parseDescription(description);
  return (
    <div className={styles.container}>
      <div className={styles.information}>
        <JobDetailed
          {...{
            title,
            salary: parsedSalary,
            date,
            description: parsedDescription,
            pictures,
            benefits,
            employment_type,
          }}
        />
      </div>
    </div>
  );
};

const mapStatetoProps = (state: RootState) => ({
  currentJob: selectCurrentJob(state),
});

export default connect(mapStatetoProps, {})(JobDetailedContainer);
