import React, { useCallback, useEffect, useState } from "react";
import { connect } from "react-redux";
import AdditionalInfo from "../JobDetailed/AdditionalInfo/AdditionalInfo";
import { usePagination } from "../redux/hooks";
import {
  JobType,
  LocationType,
  selectCurrnetTags,
  selectJobsList,
  setCurrentJob,
  setTag,
} from "../redux/jobSlice";
import { AppDispatch, RootState } from "../redux/store";
import Job from "./Job";

type JobContainerProps = {
  list: JobType[];
  tags: Array<string>;
  onSetCurrentJob: (data: JobType) => void;
  onSetTag: (tag: string) => void;
};

export const compareTags = (first: Array<any>, second: Array<any>): boolean => {
  for (const element of first) {
    if (!second.includes(element)) return false;
  }
  return true;
};

const sortJobs = (
  list: Array<JobType>,
  selectedTags: Array<string>
): Array<JobType> => {
  const res: Array<JobType> = [];
  for (const job of list) {
    const { benefits, employment_type } = job;
    const jobTags = benefits.concat(employment_type);
    if (compareTags(selectedTags, jobTags)) {
      res.push(job);
    }
  }
  return res;
};

const JobContainer: React.FC<JobContainerProps> = (
  props: JobContainerProps
) => {
  const [list, setList] = useState<JobType[]>([]);
  const [tags, setTags] = useState<string[]>([]);
  const [sortedList, setSortedList] = useState<JobType[]>([]);
  const {
    firstContentIndex,
    lastContentIndex,
    nextPage,
    prevPage,
    page,
    setPage,
    totalPages,
  } = usePagination({
    contentPerPage: 4,
    count: tags ? sortedList.length : list.length,
  });
  useEffect(() => {
    setList(props.list);
    setTags(props.tags);
    setSortedList(sortJobs(list, tags));
  }, []);
  console.log("render");

  return (
    <div>
      {!tags.length ? (
        <></>
      ) : (
        <AdditionalInfo
          {...{
            title: "Tags",
            data: [
              {
                heading: "Selected tags",
                elements: tags,
                style:
                  "max-w-1/3 min-w-fit text-center p-3 w-1/5 border border-blue-500 rounded-xl text-blue-500 bg-blue-100  hover:bg-blue-300 active:ring-4 focus:ring-blue-300",
              },
            ],
            onSetTag: props.onSetTag,
          }}
        />
      )}
      {sortedList.length
        ? sortedList
            .slice(firstContentIndex, lastContentIndex)
            .map((item: JobType, idx) => (
              <Job
                data={{ ...item }}
                onSetCurrentJob={props.onSetCurrentJob}
                key={idx}
              />
            ))
        : list.map((item: JobType, idx) => (
            <Job
              data={{ ...item }}
              onSetCurrentJob={props.onSetCurrentJob}
              key={idx}
            />
          ))}
      <div className="pagination">
        <p className="text">
          {page}/{totalPages}
        </p>
        <button onClick={prevPage} className="page">
          &larr;
        </button>
        {/* @ts-ignore */}
        {[...Array(totalPages).keys()].map((el) => (
          <button
            onClick={() => setPage(el + 1)}
            key={el}
            className={`page ${page === el + 1 ? "active" : ""}`}
          >
            {el + 1}
          </button>
        ))}
        <button onClick={nextPage} className="page">
          &rarr;
        </button>
      </div>
    </div>
  );
};

const mapStatetoProps = (state: RootState) => ({
  list: selectJobsList(state),
  tags: selectCurrnetTags(state),
});

const mapDispatchToProps = (dispatch: AppDispatch) => ({
  onSetCurrentJob: (data: JobType) => {
    dispatch(setCurrentJob(data));
  },
  onSetTag: (tag: string) => {
    dispatch(setTag(tag));
  },
});

export default React.memo(
  connect(mapStatetoProps, mapDispatchToProps)(JobContainer)
);
